import React, { HtmlHTMLAttributes } from 'react'
import classNames from 'classnames'
import './index.scss'

interface SpaceProps extends HtmlHTMLAttributes<HTMLDivElement> {
  children?: React.ReactNode[]
  style?: React.CSSProperties
  direction?: 'horizontal' | 'vertical'
  align?: 'start' | 'end' | 'center' | 'baseline'
  className?: string
}

function Space(props: SpaceProps) {
  const { children = [], direction = 'horizontal', className, ...rest } = props
  const newChildren = children?.map((el, index) => (
    <div className="ant-flex-item " key={index}>
      {el}
    </div>
  ))
  const cls = classNames('ant-flex', `flex-${direction}`, className)
  return (
    <div className={cls} {...rest}>
      {newChildren}
    </div>
  )
}

export default Space
